<template>
  <el-dropdown>
    <span class="el-dropdown-link">
      <el-avatar 
        shape="square" 
        :size="40" 
        :src="avatarUrl"
        @error="handleAvatarError"
      />
      &nbsp;&nbsp;{{ getUserName() }}
      <el-icon class="el-icon--right">
        <arrow-down/>
      </el-icon>
    </span>
    <template #dropdown>
      <el-dropdown-menu>
        <el-dropdown-item>
          <router-link :to="{name:'个人中心'}">个人中心</router-link>
        </el-dropdown-item>
        <el-dropdown-item @click="logout">安全退出</el-dropdown-item>
      </el-dropdown-menu>
    </template>
  </el-dropdown>
</template>

<script setup>
import { ref, computed, onMounted } from 'vue'
import { ArrowDown } from '@element-plus/icons-vue'
import requestUtil, {getServerUrl} from '@/util/request'
import router from '@/router'
import store from '@/store'
import { ElMessage } from 'element-plus'

// 默认头像
const defaultAvatar = 'data:image/png;base64,'

// 从存储获取用户数据
const getUserInfo = () => {
  try {
    const userString = sessionStorage.getItem("currentUser")
    if (userString) {
      return JSON.parse(userString)
    }
    return { avatar: 'default.jpg', username: 'unknown' }
  } catch (error) {
    console.error("解析用户信息失败:", error)
    return { avatar: 'default.jpg', username: 'unknown' }
  }
}

// 获取用户头像URL
const avatarUrl = computed(() => {
  const user = getUserInfo()

  try {
    let baseUrl = getServerUrl()
    // 确保baseUrl以/结尾
    if (!baseUrl.endsWith('/')) {
      baseUrl += '/'
    }

    // 使用服务器端的default.jpg作为默认头像，与其他页面保持一致
    const avatarName = user?.avatar || 'default.jpg'
    const fullUrl = `${baseUrl}media/userAvatar/${avatarName}`
    return fullUrl
  } catch (error) {
    console.error("构建头像URL失败:", error)
    // 如果构建失败，返回服务器端默认头像
    return `${getServerUrl()}media/userAvatar/default.jpg`
  }
})

// 获取用户名
const getUserName = () => {
  const user = getUserInfo()
  return user?.username || 'unknown'
}

// 头像加载失败处理
const handleAvatarError = () => {
  console.warn("头像加载失败，使用默认头像")
  return true // 使用默认头像
}

const logout = () => {
  try {
    // 使用Vuex的action清理状态
    store.commit('LOGOUT')
    
    // 为了兼容旧代码，也清理sessionStorage
    window.sessionStorage.clear()
    
    // 跳转到登录页
    router.replace("/login")
    
    ElMessage.success("已安全退出")
  } catch (error) {
    console.error("退出登录失败:", error)
    ElMessage.error("退出失败，请重试")
  }
}
</script>

<style lang="scss" scoped>
.el-dropdown-link {
  cursor: pointer;
  color: var(--el-color-primary);
  display: flex;
  align-items: center;
}
